import React from "react";
import CalculatorService from "../services/CalculatorService";


class CalculatorComponent extends React.Component{

    constructor(props){
        super(props)
        this.state={
            calculations:[]
        }
        this.numbers = {
            number1:'',
            number2:''
        }
    }

    handleFromChange1(event){
        this.numners.numbers1 = event.target.value;
    }
    handleFromChange2(event){
        this.numbers.numbers2 = event.target.value;
    }

    handleFormSubmit(){

        alert('addition clicked');

    };

    componentDidMount(){
        CalculatorService.getCalculations().then((response) => {
            this.setState({calculations: response.data})
        });
    }

    render(){
        return (
            <div>

                <form>
                    <p style={{fontSize:25, fontWeight:3}}>Calculate:
                    <input type="text"
                    number1="number1"
                    placeHolder="enter a number.."
                    onChange={this.handleFromChange1}
                    />
                    <input type="text"
                    number2="number2"
                    placeHolder="enter a number.."
                    onChange={this.handleFromChange2}
                    />
                    <button onClick={this.handleFormSubmit(this)}>Add</button>
                    <button>Subtract</button>
                    <button>Multiply</button>
                    <button>Divide</button>
                    </p>
                    <p style={{fontSize:25, fontWeight:3}}>Result:
                    <div>{this.state.calculations.result}</div> </p>
                </form>
                <br/>
                <br/>
                <h2 className="text-center">Calculations Recorded</h2>
                <table className="table table-striped">
                    <thead className="text-center">
                        <tr>
                            <td>Id</td>
                            <td>Calculation</td>
                            <td>Result</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.calculations.map(
                                calculation =>
                                <tr key = {calculation.id}>
                                        <td> {calculation.id} </td>
                                        <td> {calculation.calculation} </td>
                                        <td> {calculation.result} </td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>

            </div>
        )
    }

}

export default  CalculatorComponent
