import react from "react";
import React from "react";
import CalculatorService from "../services/CalculatorService";


class CalculatorComponent extends React.Component{

    constructor(props){
        super(props)
        this.state={
            calculations:[]
        }
    }

    componentDidMount(){
        //this.getData();
        CalculatorService.getCalculations().then((response) => {
            this.setState({calculations: response.data})
        });
    }

    // this.getData = 

    render(){
        return (
            <div>
                <h1 className="text-left"> Calculate: </h1>
                <form>
                    <input type="text"
                    name="number1:"
                    placeHolder="enter a number.."
                    />
                    <input type="text"
                    name="number2:"
                    placeHolder="enter a number.."
                    />
                    <button type="submit">Add</button>
                    <button type="submit">Subtract</button>
                    <button type="submit">Multiply</button>
                    <button type="submit">Divide</button>
                </form>
                <h2 className="text-center">Calculations Recorded</h2>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <td>Id</td>
                            <td>Calculation</td>
                            <td>Result</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.calculations.map(
                                calculation =>
                                <tr key = {calculation.id}>
                                        <td> {calculation.id} </td>
                                        <td> {calculation.calculation} </td>
                                        <td> {calculation.result} </td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>

            </div>
        )
    }

}

export default  CalculatorComponent
