import react from "react";
import React from "react";
import CalculatorService from "../services/CalculatorService";


class CalculatorComponent extends React.Component{

    constructor(props){
        super(props)
        this.state={
            calculations:[]
        }
    }

    componentDidMount(){
        //this.getData();
        CalculatorService.getCalculations().then((response) => {
            this.setState({calculations: response.data})
        });
    }

    callAddition() {
        alert('Button was pressed!');
    }
    callSubtraction() {
        alert('Button was pressed!');
    }
    callMultiplication() {
        alert('Button was pressed!');
    }
    callDivision() {
        alert('Button was pressed!');
    }

    // this.getData = 

    render(){
        return (
            <div>

                <form>
                    <p style={{fontSize:25, fontWeight:3}}>Calculate:
                    <input type="text"
                    number1="number1:"
                    placeHolder="enter a number.."
                    />
                    <input type="text"
                    number2="number2:"
                    placeHolder="enter a number.."
                    />
                    <button  onClick={this.callAddition(number1, number2)}>Add</button>
                    <button  onClick={this.callSubtraction(number1, number2)} >Subtract</button>
                    <button  onClick={this.callMultiplication(number1, number2)} >Multiply</button>
                    <button  onClick={this.callDivision(number1, number2)}>Divide</button>
                    </p>
                    <p style={{fontSize:25, fontWeight:3}}>Result:
                    <div>{this.state.calculations.result}</div> </p>
                </form>
                <br/>
                <br/>
                <h2 className="text-center">Calculations Recorded</h2>
                <table className="table table-striped">
                    <thead className="text-center">
                        <tr>
                            <td>Id</td>
                            <td>Calculation</td>
                            <td>Result</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.calculations.map(
                                calculation =>
                                <tr key = {calculation.id}>
                                        <td> {calculation.id} </td>
                                        <td> {calculation.calculation} </td>
                                        <td> {calculation.result} </td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>

            </div>
        )
    }

}

export default  CalculatorComponent
